import { useNavigate } from "react-router-dom";

export default function TopComponent() {
  const navigate = useNavigate();

  return (
    <div className="min-h-140 sm:min-h-150 flex flex-col justify-center items-center px-12 sm:px-16">
      <div
        className="flex flex-col items-center justify-center w-full bg-no-repeat bg-cover bg-center px-4 sm:px-8"
        style={{
          backgroundImage: `url('data:image/svg+xml;charset=utf-8;base64,')`,
        }}
      >
        <div className="text-center mb-6 px-2 sm:px-4">
          <h1 className="font-inter font-bold text-2xl sm:text-3xl md:text-4xl lg:text-5xl">
            Your wedding team and
            <br /> everything in between
          </h1>
        </div>

        <div className="w-full max-w-md sm:max-w-lg px-2 sm:px-4">
          <p className="text-center font-bold mb-1 sm:mb-2 text-sm sm:text-base">Sign up today!</p>
          <p className="text-center text-xs sm:text-sm font-light mb-3 sm:mb-4">
            By clicking ‘Sign up’, you agree to the Privacy Policy and Terms of Use.
          </p>

          <form className="space-y-3 sm:space-y-4">
            <div className="flex flex-col sm:flex-row sm:space-x-3 space-y-3 sm:space-y-0">
              <input
                className="border-gray-400 border w-full h-9 sm:h-10 pl-3 bg-white focus:border-black focus:outline-none text-sm sm:text-base"
                type="email"
                placeholder="Email"
              />
              <input
                className="border-gray-400 border w-full h-9 sm:h-10 pl-3 bg-white focus:border-black focus:outline-none text-sm sm:text-base"
                type="password"
                placeholder="Password"
              />
            </div>
            <button
              type="button"
              onClick={() => navigate("/signup")}
              className="w-full h-9 sm:h-10 bg-black text-white font-semibold hover:bg-gray-800 transition duration-300 text-sm sm:text-base"
            >
              Login
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}






